import { useContext, useState, useEffect } from "react";
import { AppContext } from "../context/AppContext";
const Modal = ({ show, onClose, children }: { show: boolean; onClose: () => void; children: React.ReactNode }) => {

  return show?(
    <div style={{
      position: 'fixed', 
      top: '20px', 
      left: '50%', 
      transform: 'translateX(-50%)',
      backgroundColor: '#333', 
      color: '#fff',
      borderRadius: '8px',
      padding: '20px',
      zIndex: 1000,
      width: '300px',
      textAlign: 'center',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    }}>
      <div style={{ marginBottom: '20px' }}>
        {children}
      </div>
      <button 
        onClick={onClose}>
        OK
      </button>
    </div>
  ) : null;
};

const Remaining = () => {
  const { expenses } = useContext(AppContext);
  let budget = 1000;

  const totalExpenses = expenses.reduce((total, item) => {
    return (total = total + item.cost);
  }, 0);

  const alertType = totalExpenses > budget ? "alert-danger" : "alert-success";

  // Exercise: Create an alert when Remaining is less than 0.
  const [modalVisible, setModalVisible] = useState(false);
  useEffect(() => {
    console.log("budget: ", budget);
    console.log("expenses: ", totalExpenses);

    if (budget - totalExpenses < 0) {
      console.log("show model");
      setModalVisible(true);
    }
  }, [totalExpenses]);

  // return (
  //   <div className={`alert ${alertType}`}>
  //     <span>Remaining: ${budget - totalExpenses}</span>
  //     <Modal show={modalVisible} onClose={() => setModalVisible(false)}>
  //       <h2>Warning: Budget Exceeded!</h2>
  //       <p>Your expenses have exceeded your budget!</p>
  //       <button onClick={() => setModalVisible(false)}>Ok</button>
  //     </Modal>
  //   </div>
  // );
};

export default Remaining;
